mapper.update:
UPDATE SYS_MENUS
ID = #{id}, TITLE = #{title}, MENU_KEY = #{menuKey}, MENU_LEVEL = #{menuLevel}, ICON = #{icon}, SUPER_ID = #{superId}, BTN_ID = #{btnId}, MENU_ORDER = #{menuOrder},
WHERE ID=#{id}

mapper.select:
SELECT count(1) FROM SYS_MENUS a WHERE 1=1
and ID = #{id}
and TITLE = #{title}
and MENU_KEY = #{menuKey}
and MENU_LEVEL = #{menuLevel}
and ICON = #{icon}
and SUPER_ID = #{superId}
and BTN_ID = #{btnId}
and MENU_ORDER = #{menuOrder}

SELECT A.ID,A.TITLE,A.MENU_KEY,A.MENU_LEVEL,A.ICON,B.TITLE AS SUPER_ID,
( SELECT GROUP_CONCAT( C.TITLE ) FROM SYS_BUTTONS C WHERE FIND_IN_SET ( C.ID, A.BTN_ID ) ) BTN_ID,
A.MENU_ORDER FROM SYS_MENUS A LEFT JOIN SYS_MENUS B ON A.SUPER_ID = B.ID where 1=1
and A.ID = #{id}
and A.TITLE = #{title}
and A.MENU_KEY = #{menuKey}
and A.MENU_LEVEL = #{menuLevel}
and A.ICON = #{icon}
and A.SUPER_ID = #{superId}
and A.BTN_ID = #{btnId}
and A.MENU_ORDER = #{menuOrder}
ORDER BY A.ID LIMIT ${offset}, ${limit}

SELECT ID as keyName,TITLE as titleName FROM SYS_MENUS A WHERE 1=1
and (ID = #{value} or TITLE like CONCAT('%',#{value},'%'))
ORDER BY MENU_ORDER LIMIT ${cur}, ${size}
